  			,tmp_no
  		
  		)
		values(#{apprNo},#{apprTitle},#{apprContent},#{employee.empNo},#{validDate},#{urgency},
				'C-01-0' || #{apprFinalStatus}
  		
  			,#{template.tmpNo}
  		 
		
		)

mapper.delete:
delete from approval
where appr_no in
 
  #{apprNo}
