mapper.insert:
  	 
  		select approval_seq.nextval from dual
  	
  		insert into approval(appr_no,appr_title,appr_content,emp_no,valid_date,urgency, appr_final_status
  		
  			,tmp_no
  		
  		)
		values(#{apprNo},#{apprTitle},#{apprContent},#{employee.empNo},#{validDate},#{urgency},
				'C-01-0' || #{apprFinalStatus}
  		
  			,#{template.tmpNo}
  		 
		
		)

mapper.delete:
delete from approval
where appr_no in
 
  #{apprNo}

mapper.select:
select t4.* from(select t3.* from( select rownum as rn, t2.* from( select t1.* from(select appr.appr_no, appr.appr_date ,appr.urgency, appr.appr_title , appr.complete_date, substr(appr.appr_final_status,-1,1) as appr_final_status , emp.emp_no, emp.emp_name, tmp.tmp_no ,tmp.tmp_name, (select case when emp.emp_history > 0 then (select case when ( select count(*) from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no) >0 then (select c_name from code where c_no=(select duty_no from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no)) else emp.duty end as emp_duty from dual ) else emp.duty end as emp_duty from dual) as duty , (select case when emp.emp_history > 0 then (select case when ( select count(*) from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no) > 0 then (select c_name from code where c_no=(select dept_no from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no)) else emp.department end as department from dual ) else emp.department end as department from dual) as department from approval appr, template tmp, emp_view emp where appr.emp_no = emp.emp_no and appr.tmp_no=tmp.tmp_no
and emp.emp_no=#{empNo}
and appr.appr_final_status = 'C-01-0'||#{apprFinalStatus}
union select appr.appr_no, appr.appr_date ,appr.urgency, appr.appr_title , appr.complete_date, substr(appr.appr_final_status,-1,1) as appr_final_status , emp.emp_no, emp.emp_name, tmp.tmp_no ,tmp.tmp_name, (select case when emp.emp_history> 0 then (select case when ( select count(*) from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no) >0 then (select c_name from code where c_no=(select duty_no from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no)) else emp.duty end as emp_duty from dual ) else emp.duty end as emp_duty from dual) as duty , (select case when emp.emp_history > 0 then (select case when ( select count(*) from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no) >0 then (select c_name from code where c_no=(select dept_no from code_history where start_date<=to_date(appr.appr_date,'YY/MM/DD') and end_date>=to_date(appr.appr_date,'YY/MM/DD') and emp_no=emp.emp_no)) else emp.department end as department from dual ) else emp.department end as department from dual) as department from approval appr, template tmp, emp_view emp,approval_record rec ,receiver_line line where appr.emp_no = emp.emp_no and appr.tmp_no=tmp.tmp_no and rec.appr_no=appr.appr_no and rec.line_no=line.line_no
and (line.line_emp_no=#{empNo} or rec.dep_emp_no=#{empNo})
and appr.appr_final_status = 'C-01-0'||#{apprFinalStatus}
)t1 order by appr_date desc ) t2 ) t3 ) t4
where t4.appr_Title Like '%' || #{keyword} ||'%'
where t4.appr_date<=to_date(#{keyword1},'YY/MM/DD')+1 and t4.appr_date>=to_date(#{keyword},'YY/MM/DD')
where t4.emp_name Like '%' || #{keyword} ||'%'
where t4.tmp_name Like '%' || #{keyword} ||'%'
where t4.department Like '%' || #{keyword} ||'%'
where t4.complete_date<=to_date(#{keyword1},'YY/MM/DD')+1 and t4.complete_date>=to_date(#{keyword},'YY/MM/DD')
where t4.department Like '%' || #{keyword} ||'%'
where t4.rn>=#{startRow} and t4.rn<=#{endRow}
and t4.rn>=#{startRow} and t4.rn<=#{endRow}
