mapper.select:
select * from (select temp.*, rownum rn from(select t1.tmp_no, t1.tmp_name, t1.tmp_summary, to_char(t1.tmp_date,'yyyy/mm/dd') as tmp_date, t1.tmp_using, t1.category_no, t2.CATEGORY_NAME
, (select bookmark_no from template_bookmark t3 where t1.tmp_no = t3.tmp_no and emp_no = #{empNo} ) as bookmark_no
from template t1, template_category t2 where t1.category_no = t2.category_no
and t1.category_no = #{keyword}
and t2.category_name like '%'|| #{keyword} ||'%'
and t1.tmp_name like '%'|| #{keyword} ||'%'
and t1.tmp_using = 1 order by t1.tmp_name asc) temp
order by t1.tmp_No desc) temp
where bookmark_no is not null
) where rn >= #{startRow} and rn <= #{endRow}

mapper.delete:
delete from template
where tmp_no in

	#{tmpNo}
