mapper.select:
select post_no as postNo, post_title as postTitle, writer, to_char(post_date, 'YYYY-MM-DD') as postDate, d_name as documentNo, c_name as cNo from( select rownum as rn, post1.* from (select p.post_no, p.post_title, p.writer, p.post_date, d.c_name as d_name, c.c_name, b.board_no from post p, code c, code d, board b where c.c_no = p.c_no and d.c_no = p.document_no and b.board_no = p.board_no and b.board_no= #{no}
and d.c_name like '%' || #{keyword} || '%'
and p.post_title like '%' || #{keyword} || '%'
and c.c_name like '%' || #{keyword} || '%'
and p.writer like '%' || #{keyword} || '%'
order by post_no desc )post1
where post_no is not null
) post2 where rn >= #{startRow} and rn <= #{endRow}

mapper.update:
update post

	
		, post_title = #{postTitle}
	
	
		, writer = #{writer}
	
	
		, post_date = sysdate
	
	
		, post_content = #{postContent}	
	
	
		, document_no = #{documentNo}
				
	
		, c_no = #{cNo}
			
	
where post_no = #{postNo}
