mapper.select:
	select department, duty, empName ,empNo
    from (select rownum as rn, emp.*
		      from (select *
                    from emplist_view
                    where department = #{keyfield}
	   					and empName like '%' || #{keyword} || '%'
                      order by empNo asc
                    ) emp) e
    where e.rn >= #{startRow} and e.rn <= #{endRow}

mapper.update:
update deputy
set progression = 'F', end_date = sysdate
where dep_no = #{depNo}
